import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import {increment, decrement, islogged} from './actions';
import Navbar from './components/navbar';
import Movies from './components/movies';


function App() {
  const counter = useSelector(state => state.counter);
  const isLogged = useSelector(state => state.isLogged);
  const dispatch = useDispatch();
  let movies = []
  const Movies = ({n}) => {
      for (let i = 0; i < counter; ++i) {
        movies.push(<Movies/>)
    }
  }
  return (
    <div className="App">
      <Navbar/>
      <div className="container">
        <h2>Movies {counter}</h2>
        <button onClick={() => dispatch(increment(1))}>+</button>
        <button onClick={() => dispatch(decrement(1))}>-</button>
        { isLogged ? <div className="menuright"> <button onClick={() => dispatch(islogged())}>Log out</button> </div> : <div className="menuright"><button onClick={() => dispatch(islogged())}>Log in</button></div> }
        <div className="movies">
            {movies}
        </div>
      </div>
    </div>
  );
}

export default App;
